var config={};
var mdb=require('mongoose');
var schema=mdb.Schema;

usersSchema=schema({
    name: {type: String},
    email: {type: String},
    password: {type: String}
},{timestamps:true});

loginsSchema=schema({
    username:{type:String},
    email:{type:String},
    password:{type:String}
},{timestamps:true})

config.users =mdb.model('user',usersSchema);
config.logins =mdb.model('login',loginsSchema);
module.exports = config;